using System;namespace GenerateLibrary{	/// <summary>	/// 注释特性 (只能class 和 属性 加 )	/// </summary>	[AttributeUsage(AttributeTargets.Property | AttributeTargets.Class)]	public class CommentAttribute : Attribute	{		public CommentAttribute(string name)		{			Name = name;		}		/// <summary>		/// 注释名称 		/// </summary>		public string Name { get; set; }		// 在 addDto中 显示		public bool AddShow { get; set; } = true;		// 在 updateDto中 显示		public bool UpdateShow { get; set; } = true;	}		/// <summary>	/// 搜索特性 (只能 属性 加 )	/// </summary>	[AttributeUsage(AttributeTargets.Property)]	public class SearchAttribute : Attribute	{		public SearchAttribute(SearchType searchType)		{			SearchType = searchType;		}		/// <summary>		/// 搜索类型 		/// </summary>		public SearchType SearchType { get; set; }	}	/// <summary>	/// 创建特性-用于 createDto 和 前端 vue 生成表单 form-item	/// </summary>	[AttributeUsage(AttributeTargets.Property)]	public class CreateAttribute : Attribute	{		public CreateAttribute(CreateType createType)		{			CreateType = createType;		}		/// <summary>		/// 搜索类型 		/// </summary>		public CreateType CreateType { get; set; }	}	/// <summary>	/// 显示特性(转化成Dto) (只能 属性 加 )	/// </summary>	[AttributeUsage(AttributeTargets.Property)]	public class DisplayAttribute : Attribute	{		public DisplayAttribute(DisplayType displayType)		{			DisplayType = displayType;		}		public DisplayAttribute(DisplayType displayType, string entityName)		{			DisplayType = displayType;			EntityName = entityName;		}		/// <summary>		/// 搜索类型 		/// </summary>		public DisplayType DisplayType { get; set; }		/// <summary>		/// 如果是List<T> 则需要 T的名称 		/// </summary>		public string EntityName { get; set; }	}	public enum DisplayType	{		/// <summary>		/// 文本 		/// </summary>		String,		/// <summary>		/// 数字 		/// </summary>		Int,		/// <summary>		/// 小数		/// </summary>		Decimal,		/// <summary>		/// 时间		/// </summary>		DateTime,		/// <summary>		/// bool 布尔		/// </summary>		Bool,		/// <summary>		/// guid 		/// </summary>		Guid,		/// <summary>		/// 字典 		/// </summary>		Dictionary,		/// <summary>		/// 实体 		/// </summary>		Entity,		/// <summary>		/// 实体父级		/// </summary>		EntityParent,		/// <summary>		/// 实体子级列表		/// </summary>		EntityChildrenList,		/// <summary>		/// 实体列表 		/// </summary>		EntityList,	}	/// <summary>	/// 搜索特性 (SearchDto 里面 的 搜索特性 )	/// </summary>	[AttributeUsage(AttributeTargets.Property)]	public class SearchPropAttribute : Attribute	{		public SearchPropAttribute(params string[] searchTypes)		{			SearchTypes = searchTypes;		}		/// <summary>		/// 搜索类型 		/// </summary>		public string[] SearchTypes { get; set; }	}	/// <summary>	/// 创建 类型 ,类型 不同 前端展示 不同的 form-item	/// </summary>	public enum CreateType	{		/// <summary>		/// 文本输入框		/// </summary>		StringInput,		/// <summary>		///数字输入框 		/// </summary>		NumberInput,		/// <summary>		/// 小数输入框		/// </summary>		DecimalInput,		/// <summary>		/// Bool布尔开关		/// </summary>		BoolSwitch,		/// <summary>		/// Guid选择框, 		/// </summary>		GuidSelect,		/// <summary>		///  枚举选择框		/// </summary>		EnumSelect,		/// <summary>		///  时间选择框		/// </summary>		DatePicker,	}	/// <summary>	/// 搜索的类型,根据不同类型 创建不同的前端页面 	/// </summary>	public enum SearchType	{		/// <summary>		/// 模糊搜索		/// </summary>		BlurSearch,		/// <summary>		/// Id搜索		/// </summary>		IdSearch,		/// <summary>		/// 时间区间搜索		/// </summary>		TimeBetweenSearch,	}	//	// public class LineObj	// {	// 	public LineObj(string line, int lineNumber)	// 	{	// 		Line = line;	// 		LineNumber = lineNumber;	// 	}	//	// 	public string Line { get; set; }	// 	public int LineNumber { get; set; }	// }}